import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    setIsMenuOpen(false); // Close menu on route change
  }, [location]);

  const navItems = [
    { path: '/', label: 'Home', icon: 'üè†' },
    { path: '/user-types', label: 'User Profiles', icon: 'üë•' },
    { path: '/fraud-types', label: 'Fraud Types', icon: 'üö®' },
    { path: '/quiz', label: 'Security Quiz', icon: 'üß†' },
    { path: '/resources', label: 'Resources', icon: 'üìö' }
  ];

  return (
    <header className={`nav-header ${isScrolled ? 'scrolled' : ''}`} style={{
      background: isScrolled 
        ? 'rgba(255, 255, 255, 0.15)' 
        : 'rgba(255, 255, 255, 0.1)',
      transition: 'all 0.3s ease',
      borderBottom: isScrolled ? '1px solid rgba(255, 255, 255, 0.2)' : 'none'
    }}>
      <div className="nav-container">
        <Link to="/" className="logo">
          <span style={{ fontSize: '1.5rem' }}>üõ°Ô∏è</span>
          <span style={{ 
            background: 'linear-gradient(135deg, #ffffff, #e2e8f0)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            CyberShield
          </span>
          <span style={{ 
            fontSize: '0.7rem', 
            background: '#10b981',
            color: 'white',
            padding: '2px 6px',
            borderRadius: '8px',
            marginLeft: '8px',
            fontWeight: '600'
          }}>
            INDIA
          </span>
        </Link>

        {/* Desktop Navigation */}
        <nav className="desktop-nav">
          <ul className="nav-links">
            {navItems.map((item) => (
              <li key={item.path}>
                <Link 
                  to={item.path}
                  className={location.pathname === item.path ? 'active' : ''}
                  style={{
                    background: location.pathname === item.path ? 'rgba(255, 255, 255, 0.15)' : 'transparent',
                    borderRadius: '8px'
                  }}
                >
                  <span style={{ marginRight: '6px' }}>{item.icon}</span>
                  {item.label}
                </Link>
              </li>
            ))}
          </ul>
        </nav>

        {/* Mobile Menu Button */}
        <button
          className="mobile-menu-toggle"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          style={{
            display: 'none',
            background: 'rgba(255, 255, 255, 0.1)',
            border: '1px solid rgba(255, 255, 255, 0.2)',
            borderRadius: '8px',
            color: 'white',
            padding: '8px',
            cursor: 'pointer',
            fontSize: '1.2rem'
          }}
        >
          {isMenuOpen ? '‚úï' : '‚ò∞'}
        </button>

        {/* Emergency Button */}
        <a 
          href="tel:1930" 
          className="btn btn-danger"
          style={{ 
            padding: '8px 16px',
            fontSize: '0.9rem',
            display: 'flex',
            alignItems: 'center',
            gap: '6px',
            animation: 'pulse 2s infinite'
          }}
        >
          üö® Emergency: 1930
        </a>
      </div>

      {/* Mobile Navigation Menu */}
      <div 
        className="mobile-menu"
        style={{
          display: isMenuOpen ? 'block' : 'none',
          position: 'absolute',
          top: '100%',
          left: '0',
          right: '0',
          background: 'rgba(31, 41, 55, 0.95)',
          backdropFilter: 'blur(20px)',
          borderTop: '1px solid rgba(255, 255, 255, 0.1)',
          padding: '20px',
          zIndex: 1000
        }}
      >
        <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
          {navItems.map((item) => (
            <li key={item.path} style={{ marginBottom: '12px' }}>
              <Link 
                to={item.path}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '12px',
                  padding: '12px 16px',
                  color: 'white',
                  textDecoration: 'none',
                  borderRadius: '8px',
                  background: location.pathname === item.path ? 'rgba(37, 99, 235, 0.2)' : 'transparent',
                  border: location.pathname === item.path ? '1px solid rgba(37, 99, 235, 0.3)' : '1px solid transparent',
                  transition: 'all 0.3s ease'
                }}
                onClick={() => setIsMenuOpen(false)}
              >
                <span style={{ fontSize: '1.2rem' }}>{item.icon}</span>
                <span style={{ fontWeight: '500' }}>{item.label}</span>
              </Link>
            </li>
          ))}
          
          {/* Mobile Emergency Section */}
          <li style={{ 
            marginTop: '20px', 
            padding: '16px', 
            background: 'rgba(220, 38, 38, 0.1)',
            borderRadius: '8px',
            border: '1px solid rgba(220, 38, 38, 0.3)'
          }}>
            <div style={{ marginBottom: '12px', color: '#fca5a5', fontWeight: '600' }}>
              üö® Emergency Contacts
            </div>
            <div style={{ display: 'grid', gap: '8px' }}>
              <a href="tel:1930" style={{ color: 'white', textDecoration: 'none' }}>
                üìû Cyber Crime: 1930
              </a>
              <a href="tel:155260" style={{ color: 'white', textDecoration: 'none' }}>
                üí∞ Financial Fraud: 155260
              </a>
            </div>
          </li>
        </ul>
      </div>

      {/* Mobile Menu Overlay */}
      {isMenuOpen && (
        <div 
          className="mobile-overlay"
          style={{
            position: 'fixed',
            top: '0',
            left: '0',
            right: '0',
            bottom: '0',
            background: 'rgba(0, 0, 0, 0.5)',
            zIndex: 999
          }}
          onClick={() => setIsMenuOpen(false)}
        />
      )}

      <style jsx>{`
        @media (max-width: 768px) {
          .desktop-nav {
            display: none;
          }
          
          .mobile-menu-toggle {
            display: block !important;
          }
          
          .nav-container .btn {
            padding: 6px 12px;
            fontSize: 0.8rem;
          }
        }
        
        @media (min-width: 769px) {
          .mobile-menu {
            display: none !important;
          }
        }
      `}</style>
    </header>
  );
};

export default Header;
