import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Home from './components/Home';
import UserTypes from './components/UserTypes';
import FraudTypes from './components/FraudTypes';
import QuizComponent from './components/QuizComponent';
import RealTimeAlerts from './components/RealTimeAlerts';
import Resources from './components/Resources';
import Footer from './components/Footer';

function App() {
  const [selectedUserType, setSelectedUserType] = useState('');
  const [alertsEnabled, setAlertsEnabled] = useState(false);

  useEffect(() => {
    // Simulate real-time alerts
    if (alertsEnabled) {
      const interval = setInterval(() => {
        // This would connect to a real fraud detection API
        console.log('Checking for new fraud alerts...');
      }, 30000); // Check every 30 seconds

      return () => clearInterval(interval);
    }
  }, [alertsEnabled]);

  return (
    <div className="App">
      <Header />
      <RealTimeAlerts enabled={alertsEnabled} setEnabled={setAlertsEnabled} />
      
      <Routes>
        <Route path="/" element={<Home />} />
        <Route 
          path="/user-types" 
          element={
            <UserTypes 
              selectedUserType={selectedUserType}
              setSelectedUserType={setSelectedUserType}
            />
          } 
        />
        <Route path="/fraud-types" element={<FraudTypes />} />
        <Route 
          path="/quiz" 
          element={<QuizComponent userType={selectedUserType} />} 
        />
        <Route path="/resources" element={<Resources />} />
      </Routes>
      
      <Footer />
    </div>
  );
}

export default App;
