import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Footer = () => {
  const [subscriberCount, setSubscriberCount] = useState(125847);
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    // Update time every second
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    // Simulate growing subscriber count
    const subscriberInterval = setInterval(() => {
      setSubscriberCount(prev => prev + Math.floor(Math.random() * 3));
    }, 30000);

    return () => {
      clearInterval(timeInterval);
      clearInterval(subscriberInterval);
    };
  }, []);

  const footerSections = [
    {
      title: 'Protection Services',
      links: [
        { name: 'Personal Security Assessment', path: '/quiz', icon: 'üîí' },
        { name: 'Real-time Threat Alerts', path: '/alerts', icon: 'üö®' },
        { name: 'User-specific Guidelines', path: '/user-types', icon: 'üë•' },
        { name: 'Fraud Type Analysis', path: '/fraud-types', icon: 'üîç' }
      ]
    },
    {
      title: 'Resources & Support',
      links: [
        { name: 'Emergency Helplines', path: '/resources', icon: 'üìû' },
        { name: 'Verification Tools', path: '/resources', icon: 'üõ†Ô∏è' },
        { name: 'Educational Materials', path: '/resources', icon: 'üìö' },
        { name: 'Community Forum', path: '/community', icon: 'üí¨' }
      ]
    },
    {
      title: 'Legal & Compliance',
      links: [
        { name: 'Privacy Policy', path: '/privacy', icon: 'üîê' },
        { name: 'Terms of Service', path: '/terms', icon: 'üìã' },
        { name: 'Data Protection', path: '/data-protection', icon: 'üõ°Ô∏è' },
        { name: 'Compliance Reports', path: '/compliance', icon: 'üìä' }
      ]
    }
  ];

  const emergencyContacts = [
    { name: 'Cyber Crime Helpline', number: '1930', type: 'General' },
    { name: 'Financial Fraud', number: '155260', type: 'Banking' },
    { name: 'Banking Ombudsman', number: '14448', type: 'Banking' },
    { name: 'CERT-In Emergency', number: '1800-11-4949', type: 'Technical' }
  ];

  const certifications = [
    { name: 'ISO 27001', description: 'Information Security' },
    { name: 'Digital India', description: 'Government Certified' },
    { name: 'RBI Approved', description: 'Banking Compliance' },
    { name: 'CERT-In Partner', description: 'Cyber Security' }
  ];

  return (
    <footer className="footer">
      <div className="container">
        {/* Top Section with Live Stats */}
        <div className="footer-top" style={{ 
          background: 'rgba(255, 255, 255, 0.05)',
          borderRadius: '12px',
          padding: '24px',
